// CreateDoctorSchedule.js
import React, { useState } from 'react';
import { adminApi, setAuthToken } from '../../api';
import { useLocation } from 'react-router-dom';

const CreateDoctorSchedule = ({ onCreateSchedule }) => {
  const [scheduleData, setScheduleData] = useState({
    DoctorId: '',
    Date_DateOnly: '',
    StartTime_TimeOnly: '',
    EndTime_TimeOnly: '',
  });

  // Extract doctorId from location state
  const location = useLocation();
  const { doctorId } = location.state;

  // Set the extracted doctorId in the scheduleData
  scheduleData.DoctorId = doctorId;

  const handleChange = (e) => {
    setScheduleData({
      ...scheduleData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Set the authentication token before making the API request
    setAuthToken();

    // Perform validation
    if (!scheduleData.Date_DateOnly || !scheduleData.StartTime_TimeOnly || !scheduleData.EndTime_TimeOnly) {
      // Handle empty fields
      alert('All fields are required.');
      return;
    }

    const currentDate = new Date();
    const selectedDate = new Date(scheduleData.Date_DateOnly + 'T' + scheduleData.StartTime_TimeOnly);

    if (selectedDate < currentDate) {
      // Handle past dates
      alert('Please select a date and time in the future.');
      return;
    }

    if (scheduleData.StartTime_TimeOnly >= scheduleData.EndTime_TimeOnly) {
      // Handle invalid time range
      alert('End time must be after start time.');
      return;
    }

    try {
      // Call the provided onCreateSchedule function with the schedule data
      await adminApi.createDoctorSchedule(scheduleData);
      // Optionally, you can perform additional actions after successful schedule creation
      console.log('API Doctor', scheduleData);
      alert('Doctor schedule created successfully.');
    } catch (error) {
      console.error('Error creating doctor schedule:', error);
      // Handle the error (display an error message, etc.)
    }
  };

  return (
    <div>
      <h2>Create Doctor Schedule</h2>
      <form onSubmit={handleSubmit}>
        <label>Date:</label>
        <input
          type="date"
          name="Date_DateOnly"
          value={scheduleData.Date_DateOnly}
          onChange={handleChange}
        />
        <br />

        <label>Start Time:</label>
        <input
          type="time"
          name="StartTime_TimeOnly"
          value={scheduleData.StartTime_TimeOnly}
          onChange={handleChange}
        />
        <br />

        <label>End Time:</label>
        <input
          type="time"
          name="EndTime_TimeOnly"
          value={scheduleData.EndTime_TimeOnly}
          onChange={handleChange}
        />
        <br />

        Additional fields
        <label>Location:</label>
        <input
          type="text"
          name="Location"
          value={scheduleData.Location}
          onChange={handleChange}
        />
        <br />

        <label>Description:</label>
        <textarea
          name="Description"
          value={scheduleData.Description}
          onChange={handleChange}
        />
        <br />

        {/* Add other fields as needed */}
        <button type="submit">Create Schedule</button>
      </form>
    </div>
  );
};

export default CreateDoctorSchedule;
