import React, { useState } from 'react';
import { doctorAPi, setAuthToken } from '../../api';

const EditAppointment = ({ appointment, onUpdateSuccess, onCancel }) => {
  setAuthToken();

  const [editedDescription, setEditedDescription] = useState(appointment.description);

  const handleUpdateAppointment = async () => {
    try {
      const updateAppointment = {
        appointmentId: appointment.appointmentId,
        description: editedDescription,
      };

      await doctorAPi.checkAppointment(updateAppointment);
      onUpdateSuccess();
    } catch (error) {
      console.error('Error updating appointment:', error);
    }
  };

  return (
    <div>
      <h3>Edit Appointment</h3>
      <p>Appointment Date: {appointment.appointmentDate}</p>
      <p>Start Time: {appointment.startTime}</p>
      <label htmlFor="editedDescription">Description:</label>
      <input
        type="text"
        id="editedDescription"
        value={editedDescription}
        onChange={(e) => setEditedDescription(e.target.value)}
      />
      <button onClick={handleUpdateAppointment}>Save Changes</button>
      <button onClick={onCancel}>Cancel</button>
    </div>
  );
};

export default EditAppointment;
