// Login.js
import React, { useState } from 'react';
import { toast } from 'react-toastify';
import { authApi } from '../../api';
import { useUser } from '../../UserContext';

const Login = () => {
    const { updateUserRole } = useUser();
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const isPasswordValid = (password) => {
    
        const passwordRegex = /^(?=.*?[A-Z])(?=.*?[a-z])(?=.*?[0-9])(?=.*[#$^+=!*()@%&]).{6,}$/;
        return passwordRegex.test(password);
    };

    const isUsernameValid = (username) => {
       
        return username.trim() !== ''; // Example: Ensure username is not empty
    };

    const handleLogin = async (e) => {
        e.preventDefault(); // Prevent default form submission behavior

        try {
            if (!isUsernameValid(username)) {
                toast.error('Please enter a valid username.');
                return;
            }

            if (!isPasswordValid(password)) {
                toast.error('Please enter a valid password.');
                return;
            }

            const credentials = { username, password };
            const response = await authApi.login(credentials);

            // Assuming your API returns a JWT token
            const jwtToken = response.data.loginInfo.token;
            const userRole = response.data.loginInfo.user.userRole;
            const Name = response.data.loginInfo.user.name;
                            
            // Update user role in the context
            updateUserRole(userRole);

            // Store the token and user role in localStorage
            localStorage.setItem('jwtToken', jwtToken);
            localStorage.setItem('UserRole', userRole);
            localStorage.setItem('name', Name);

            toast.success('Login successful.');
            console.log('User Role:', userRole);
            console.log('Login successful:', response);

            // Handle successful login (e.g., redirect to dashboard)
        } catch (error) {
            console.error('Login failed:', error);
            // Handle login error (e.g., display error message)
        }
    };

    return (
        <div>
            <h2>Login</h2>
            <form>
                <label>Username:</label>
                <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
                <br />
                <label>Password:</label>
                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
                <br />
                <button type="button" onClick={handleLogin}>Login</button>
            </form>
        </div>
    );
};

export default Login;
