import React, { useState, useEffect } from 'react';
import { doctorAPi, setAuthToken } from '../../api';

const DisplayDoctorAppointments = () => {
  const [appointments, setAppointments] = useState([]);

  setAuthToken(); // Assuming this function sets the authentication token

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        // Fetch doctor appointments
        const response = await doctorAPi.displayDoctorAppointments();
        setAppointments(response.data.appointments);
        console.log('Doctor Appointments:', response.data.appointments);
      } catch (error) {
        console.error('Error fetching doctor appointments:', error);
      }
    };

    fetchAppointments();
  }, []);

  const handleCancelAppointment = async (appointmentId) => {
    try {
      // Cancel appointment based on appointmentId
      await doctorAPi.cancelAppointment(appointmentId);
      // Update appointments after cancellation
      const updatedAppointments = appointments.filter(appointment => appointment.appointmentId !== appointmentId);
      setAppointments(updatedAppointments);
      console.log('Appointment canceled successfully.');
    } catch (error) {
      console.error('Error canceling appointment:', error);
    }
  };

  return (
    <div>
      <h2>Your Appointments</h2>
      {appointments.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Appointment Date</th>
              <th>Start Time</th>
              <th>End Time</th>
              <th>Description</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {appointments.map(appointment => (
              <tr key={appointment.appointmentId}>
                <td>{appointment.appointmentDate}</td>
                <td>{appointment.startTime}</td>
                <td>{appointment.endTime}</td>
                <td>{appointment.description}</td>
                <td>
                  <button onClick={() => handleCancelAppointment(appointment.appointmentId)}>
                    Cancel Appointment
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No appointments available.</p>
      )}
    </div>
  );
};

export default DisplayDoctorAppointments;
