import React, { useState, useEffect } from 'react';
import { doctorAPi, setAuthToken } from '../../api';
import { useLocation, useNavigate } from 'react-router-dom';

const EditAppointment = () => {
  const [appointment, setAppointment] = useState({});
  const [editedDescription, setEditedDescription] = useState('');
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const { state } = location;
    if (state && state.appointmentId) {
      // Fetch the specific appointment based on the ID
      const fetchAppointment = async () => {
        try {
          const response = await doctorAPi.getAppointmentById(state.appointmentId);
          setAppointment(response.data);
          setEditedDescription(response.data.description);
        } catch (error) {
          console.error('Error fetching appointment:', error);
        }
      };

      fetchAppointment();
    }
  }, [location]);

  const handleSaveChanges = async () => {
    try {
      const updateAppointment = {
        appointmentId: appointment.appointmentId,
        description: editedDescription,
      };

      await doctorAPi.updateAppointment(updateAppointment);
      console.log('Appointment updated successfully.');
      // You can redirect to the doctor appointments page or perform other actions
      navigate('/display-doctor-appointments');
    } catch (error) {
      console.error('Error updating appointment:', error);
    }
  };

  const handleCancel = () => {
    // Redirect to the doctor appointments page without saving changes
    navigate('/display-doctor-appointments');
  };

  return (
    <div>
      <h2>Edit Appointment</h2>

      <label>Description:</label>
      <textarea
        value={editedDescription}
        onChange={(e) => setEditedDescription(e.target.value)}
      />
      <button onClick={handleSaveChanges}>Save Changes</button>
      <button onClick={handleCancel}>Cancel</button>
    </div>
  );
};

export default EditAppointment;
