// AdminDashboard.js

import { adminApi, setAuthToken } from '../../api'; // Update the path
import React, { useState } from 'react';

const mapSpecialtyToName = (specialty) => {
    switch (specialty) {
      case 0:
        return 'None';
      case 1:
        return 'Home';
      case 2:
        return 'ENT';
        case 3:
        return 'Dermatologist';
        case 4:
           return 'Ophtalmologist'; 
        case 5:
            return 'Neurologist';
        case 6:
            return 'Orthopedist';
        case 7:
           return ' Pediatrician';
   
      default:
        return 'None';
    }
  };
const DoctorRegistration = () => {
    const [name, setName] = useState('');
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [passwordConfirm, setPasswordConfirm] = useState('');
    const [specialty, setSpecialty] = useState(0);

  const handleDoctorRegistration = async () => {
    const doctorData = {
        name,
        username,
        email,
        password,
        passwordConfirm,
        specialty,
      };
  
      // Convert specialty to user-friendly name before sending to the backend
      const userFriendlySpecialty = mapSpecialtyToName(specialty);
      console.log('User-friendly Specialty:', userFriendlySpecialty);
    // Set JWT token in headers before making any admin API requests
    setAuthToken();

    try {
      // Use the registerDoctor API function to register a doctor
      const response = await adminApi.registerDoctor(doctorData);

      // Handle the response as needed
      console.log('Doctor registration successful:', response.data);

      // You can reset the form or take any other actions after successful registration
      setDoctorData({
        name: '',
        username: '',
        email: '',
        password: '',
        passwordConfirm: '',
        specialty: 0,
      });
    } catch (error) {
      console.error('Error registering doctor:', error);
      // Handle the error (display an error message, etc.)
    }
  };

  return (
    <div>
      <h2>Doctor Registration</h2>
      {/* Doctor Registration Form */}
      <form>
        <label>Name:</label>
        <input
          type="text"
          value={doctorData.name}
          onChange={(e) => setDoctorData({ ...doctorData, name: e.target.value })}
        />
        <br />

        <label>Username:</label>
        <input
          type="text"
          value={doctorData.username}
          onChange={(e) => setDoctorData({ ...doctorData, username: e.target.value })}
        />
        <br />

        <label>Email:</label>
        <input
          type="email"
          value={doctorData.email}
          onChange={(e) => setDoctorData({ ...doctorData, email: e.target.value })}
        />
        <br />

        <label>Password:</label>
        <input
          type="password"
          value={doctorData.password}
          onChange={(e) => setDoctorData({ ...doctorData, password: e.target.value })}
        />
        <br />

        <label>Confirm Password:</label>
        <input
          type="password"
          value={doctorData.passwordConfirm}
          onChange={(e) => setDoctorData({ ...doctorData, passwordConfirm: e.target.value })}
        />
        <br />

        <label>Specialty:</label>
        <input
          type="number"
          value={doctorData.specialty}
          onChange={(e) => setDoctorData({ ...doctorData, specialty: parseInt(e.target.value, 10) })}
        />
        <br />

        {/* Add other fields for doctor registration as needed */}
        
        <button type="button" onClick={handleDoctorRegistration}>
          Register Doctor
        </button>
      </form>
    </div>
  );
};

export default DoctorRegistration;
