// SearchDoctor.js
import React, { useState } from 'react';
import { patientAPi, setAuthToken } from "../../api";
import { toast } from 'react-toastify';
const specialties = [
    { value: 0, label: 'None' },
    { value: 1, label: 'Home' },
    { value: 2, label: 'ENT' },
    { value: 3, label: 'Dermatologist' },
    { value: 4, label: 'Ophtalmologist' },
    { value: 5, label: 'Neurologist' },
    { value: 6, label: 'Orthopedist' },
    { value: 7, label: 'Pediatrician' },
  ];
const SearchDoctor = () => {
  const [specialty, setSpecialty] = useState('');
  const [doctors, setDoctors] = useState([]);
  setAuthToken();

  const handleSearch = async () => {
    try {
      // Fetch doctors by specialty
      const response = await patientAPi.searchDoctor(specialty);
      setDoctors(response.data.doctorUsers);
      console.log('Doctors:', response.data);

      if (response.data.doctorUsers.length === 0) {
        toast.info('No doctors found for the specified specialty.');
      }
    } catch (error) {
      console.error('Error searching doctors:', error);
      toast.error('Error searching doctors. Please try again.');
    }
  };
  const handleCheckAvailability = async (doctorId) => {
    try {
      // Fetch doctor availability
      const response = await patientAPi.displayDoctorAvailability(doctorId);
      // Display availability information (you can customize this part based on your API response)
      console.log('Doctor Availability:', response.data);
      toast.success('Availability checked successfully.');
    } catch (error) {
      console.error('Error checking availability:', error);
      toast.error('Error checking availability. Please try again.');
    }
  };
  return (
    <div>
      <h2>Search Doctors by Specialty</h2>
      <label>Specialty:</label>
      <select value={specialty} onChange={(e) => setSpecialty(parseInt(e.target.value, 10))}>
        {specialties.map((spec) => (
          <option key={spec.value} value={spec.value}>
            {spec.label}
          </option>
        ))}
      </select>
      <button onClick={handleSearch}>Search</button>

      {doctors.length > 0 && (
        <div>
          <h3>Results</h3>
          <table>
            <thead>
              <tr>
                <th>Doctor Name</th>
                <th>Specialization</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {doctors.map((doctor) => (
                <tr key={doctor.doctorId}>
                  <td>{doctor.name}</td>
                  <td>{doctor.specialization}</td>
                  <td>
                    <button onClick={() => handleCheckAvailability(doctor.doctorId)}>
                      Check Availability
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
              };
export default SearchDoctor;
