import React, { useState, useEffect } from 'react';
import { patientAPi, setAuthToken } from '../../api';
import { useLocation } from 'react-router-dom';

const AllSlots = () => {
  const [availableTimeSlots, setAvailableTimeSlots] = useState([]);
  const location = useLocation();
  const { doctorId, doctordate } = location.state;
  setAuthToken();

  useEffect(() => {
    const fetchAvailableTimeSlots = async () => {
      try {
        // Fetch available time slots based on doctorId and date
        const response = await patientAPi.dispalayAvailableSlots(doctorId, doctordate);
        setAvailableTimeSlots(response.data.availableTimeSlots);
        console.log('Available Time Slots:', response.data);
      } catch (error) {
        console.error('Error fetching available time slots:', error);
      }
    };

    fetchAvailableTimeSlots();
  }, [doctorId, doctordate]);

  const handleBookSlot = async (appointmentDate, startTime) => {
    console.log('Doctor Id:', doctorId);
    console.log('Appointment Date:', doctordate);
    console.log('Start Time:', startTime);
    try {
      const doctorId = location.state.doctorId; // Assuming you have doctorId available in the component state
  
      if (!doctorId) {
        console.error('DoctorId is missing.');
        return;
      }
  
      // Logic for booking a slot
      const response = await patientAPi.bookAppointment(doctorId, appointmentDate, startTime);
      console.log('Appointment booked successfully:', response.data);
    } catch (error) {
      console.error('Error booking appointment:', error);
      if (error.response) {
        console.error('Server responded with:', error.response.data);
      }
    }
  };
  
  

  return (
    <div>
      <h2>Available Time Slots</h2>
      {availableTimeSlots.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Date</th>
              <th>Start Time</th>
              <th>End Time</th>
            </tr>
          </thead>
          <tbody>
            {availableTimeSlots.map((slot, index) => (
              <tr key={index}>
                <td>{slot.appointmentDate}</td>
                <td>{slot.startTime}</td>
                <td>{slot.endTime}</td>
                <td>
                <button onClick={() => handleBookSlot(doctorId, slot.appointmentDate, slot.startTime)}>
  Book Slot
</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No available time slots.</p>
      )}

     
    </div>
  );
};

export default AllSlots;
