import React, { useState, useEffect } from 'react';
import { doctorAPi, setAuthToken } from '../../api';

const DisplayDoctorAppointments = () => {
  const [appointments, setAppointments] = useState([]);
  const [checkingAppointmentId, setCheckingAppointmentId] = useState(null);
  const [checkedDescription, setCheckedDescription] = useState('');

  setAuthToken();

  const fetchAppointments = async () => {
    try {
      const response = await doctorAPi.displayDoctorAppointments();
      setAppointments(response.data.appointments);
      console.log('Doctor Appointments:', response.data.appointments);
    } catch (error) {
      console.error('Error fetching doctor appointments:', error);
    }
  };

  useEffect(() => {
    fetchAppointments();
  }, []);

  const handleCheckAppointment = (appointmentId) => {
    setCheckingAppointmentId(appointmentId);
    const appointmentToCheck = appointments.find(appointment => appointment.appointmentId === appointmentId);
    setCheckedDescription(appointmentToCheck.description);
  };

  const handleUpdateCheckedAppointment = async () => {
    try {
      const updateAppointment = {
        appointmentId: checkingAppointmentId,
        description: checkedDescription,
      };
      console.log('updateAppointment:', updateAppointment);
      const response= await doctorAPi.checkAppointment(updateAppointment);
      console.log('Appointment checked successfully.', response.data);
      setCheckingAppointmentId(null);
      setCheckedDescription('');
      fetchAppointments();
      console.log('Appointment checked successfully.', response.data);
    } catch (error) {
      console.error('Error checking appointment:', error);
    }
  };

  return (
    <div>
      <h2>Your Appointments</h2>
      {appointments.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Date</th>
              <th>Start Time</th>
              <th>Description</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {appointments.map(appointment => (
              <tr key={appointment.appointmentId}>
                <td>{appointment.appointmentDate}</td>
                <td>{appointment.startTime}</td>
                <td>
                  {checkingAppointmentId === appointment.appointmentId ? (
                    <input
                      type="text"
                      value={checkedDescription}
                      onChange={(e) => setCheckedDescription(e.target.value)}
                    />
                  ) : (
                    appointment.description
                  )}
                </td>
                <td>
                  {checkingAppointmentId === appointment.appointmentId ? (
                    <button onClick={handleUpdateCheckedAppointment}>Save Changes</button>
                  ) : (
                    <button onClick={() => handleCheckAppointment(appointment.appointmentId)}>
                      Check Appointment
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No appointments available.</p>
      )}
    </div>
  );
};

export default DisplayDoctorAppointments;
