// AdminDashboard.js

import { adminApi, setAuthToken } from '../../api'; // Update the path
import React, { useState } from 'react';

const mapSpecialtyToName = (specialty) => {
  switch (specialty) {
    case 0:
      return 'None';
    case 1:
      return 'Home';
    case 2:
      return 'ENT';
    case 3:
      return 'Dermatologist';
    case 4:
      return 'Ophtalmologist';
    case 5:
      return 'Neurologist';
    case 6:
      return 'Orthopedist';
    case 7:
      return 'Pediatrician';
    default:
      return 'None';
  }
};

const DoctorRegistration = () => {
  const [name, setName] = useState('');
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [passwordConfirm, setPasswordConfirm] = useState('');
  const [specialty, setSpecialty] = useState(0);

  const handleDoctorRegistration = async () => {
    // Set JWT token in headers before making any admin API requests
    setAuthToken();
    const userFriendlySpecialty = mapSpecialtyToName(specialty);
    console.log('User-friendly Specialty:', userFriendlySpecialty);
    try {
      // Use the registerDoctor API function to register a doctor
      const response = await adminApi.registerDoctor({
        name,
        username,
        email,
        password,
        passwordConfirm,
        specialty,
      });

      // Handle the response as needed
      console.log('Doctor registration successful:', response.data);

      // You can reset the form or take any other actions after successful registration
      setName('');
      setUsername('');
      setEmail('');
      setPassword('');
      setPasswordConfirm('');
      setSpecialty(0);
    } catch (error) {
      console.error('Error registering doctor:', error);
      // Handle the error (display an error message, etc.)
    }
  };

  return (
    <div>
      <h2>Doctor Registration</h2>
      {/* Doctor Registration Form */}
      <form>
        <label>Name:</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        <br />

        <label>Username:</label>
        <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
        <br />

        <label>Email:</label>
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        <br />

        <label>Password:</label>
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        <br />

        <label>Confirm Password:</label>
        <input
          type="password"
          value={passwordConfirm}
          onChange={(e) => setPasswordConfirm(e.target.value)}
        />
        <br />

        <label>Specialty:</label>
        <input
          type="number"
          value={userFriendlySpecialty}
          onChange={(e) => setSpecialty(parseInt(e.target.value, 10))}
        />
        <br />

        {/* Add other fields for doctor registration as needed */}

        <button type="button" onClick={handleDoctorRegistration}>
          Register Doctor
        </button>
      </form>
    </div>
  );
};

export default DoctorRegistration;
