// AppHeader.js
import React from 'react';
import { Link } from 'react-router-dom';
import { useUser } from './UserContext';
import Logout from './components/auth/Logout'; // Make sure to import your Logout component
const [displayusername, displayusernameupdate] = useState('');
const [showmenu, showmenuupdate] = useState(false);
const usenavigate = useNavigate();
const location = useLocation();

useEffect(() => {
    if (location.pathname === '/login' || location.pathname === '/register') {
        showmenuupdate(false);
    } else {
        showmenuupdate(true);
        let name = localStorage.getItem('name');
        if (name === '' || name === null) {
            usenavigate('/login');
        } else {
            displayusernameupdate(name); // Update state with the name
        }
    }
}, [location.pathname, usenavigate]);

const AppHeader = () => {
  const { userRole} = useUser();

  return (
    <header>
      <nav>
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          {userRole === 'Patient' && (
            <li>
              <Link to="/patient">Patient</Link>
            </li>
          )}
          {userRole === 'Doctor' && (
            <li>
              <Link to="/doctor">Doctor</Link>
            </li>
          )}
          {userRole === 'Admin' && (
            <li>
              <Link to="/admin">Admin Panel</Link>
            </li>
          )}
          <li>
            {userRole && (
              <span style={{ marginLeft: '70%' }}>Welcome <b>{displayName}</b></span>
            )}
            <Logout />
          </li>
          {!userRole && (
            <>
              <li>
                <Link to="/login">Login</Link>
              </li>
              <li>
                <Link to="/register">Register</Link>
              </li>
            </>
          )}
        </ul>
      </nav>
    </header>
  );
};

export default AppHeader;
