import React, { useState, useEffect } from 'react';
import { doctorAPi, setAuthToken } from '../../api';
import { useLocation, useNavigate } from 'react-router-dom';
const EditAppointment = () => {
    const [appointment, setAppointment] = useState({
      appointmentId: '',
      description: '',
    });
    const navigate = useNavigate();
    const location = useLocation();
  
    setAuthToken();
  
    
    useEffect(() => {
      const appointmentId = location?.state?.appointmentId;
      setAppointment((prev) => ({ ...prev, appointmentId }));
    }, [location]);
  
    const handleSaveChanges = async () => {
      try {
        console.log('Sending update request:', appointment);
  
      
        if (!appointment.appointmentId || !appointment.description) {
          console.error('AppointmentId and Description are required.');
          return;
        }
  
        await doctorAPi.checkAppointment(appointment);
        console.log('Appointment updated successfully.');

        navigate('/Display-doctor-appointments');
      } catch (error) {
        console.error('Error updating appointment:', error);
        if (error.response) {
          console.error('Server responded with:', error.response.data);
          const validationErrors = error.response.data.errors;
          if (validationErrors) {
            console.error('Validation errors:', validationErrors);
          }
        }
      }
    };
  
    return (
      <div>
        <h2>Edit Appointment</h2>
        <label>Description:</label>
        <textarea
          value={appointment.description}
          onChange={(e) => setAppointment({ ...appointment, description: e.target.value })}
        />
        <button onClick={handleSaveChanges}className="blue-button">Save Changes</button>
        <button onClick={() => navigate('/Display-doctor-appointments')}>Cancel</button>
      </div>
    );
  };
  
  export default EditAppointment;
